from nose.tools import *
from bio import Genome
from mock import patch


def test_most_repeated_rough():
    assert_equal(set(['GATG', 'ATGC', 'ATGT']), Genome('ACGTTGCATGTCGCATGATGCATGAGAGCT').most_repeated_rough(4, 1))
    #assert_equal(set(['GCACACAGAC', 'GCGCACACAC']), Genome('').most_repeated_rough(10, 2))

def test_most_repeated_rough_with_reverse_complements():
    assert_equal(set(['ATGT', 'ACAT']), Genome('ACGTTGCATGTCGCATGATGCATGAGAGCT').most_repeated_rough_with_reverse_complements(4, 1))
